import BookmarkList from './BookmarkList'
import './List.css'
export default function List({item, book, setBook}){
  const marking=(e)=>{
    console.log(book)
    function booking(){book.push({
      id: item.id,
      image_url: item.image_url || item.brand_image_url,
      title: item.title || item.brand_name,
      discountPercentage: item.discountPercentage,
      follower: item.follower? item.follower : item.price+`원`,
      sub_title: item.sub_title
    })}
    setBook(booking())
    console.log(book)
    switch(e.target.src){
      case '../onStar.png'
        : e.target.src = '../offStar.png'
      case '../offStar.png'
        : e.target.src = '../onStar.png'
    }
  }
  return(
    <section id='itemBox'>
      <div className='itemImg'>
        <img className='itemImg' src={item.image_url || item.brand_image_url} alt={item.title || item.brand_name} />
        <FontAwesomeIcon id={item.id} className='bookmarker' onClick={marking} icon={faStar} beat />
      </div>
      <div className='description'>
        <span className='title'>{item.title || item.brand_name}</span>
        {item.discountPercentage? <span className='persent'>{item.discountPercentage+`%`}</span> : null}
        {item.follower? <span className='title'>관심고객수</span> : null}
      </div>
      <div className='description'>
        <span className='sub'>{item.sub_title}</span>
        <span className='follower'>{item.price? item.price+`원` : item.follower}</span>
      </div>
    </section>
  )
}
