import Main from './page/Main';
import ProdectsList from './page/ProductList';
import Bookmark from './page/Bookmark';

import Header from './component/Header';
import Footer from './component/Footer';

import { useEffect, useState } from 'react';
import {BrowserRouter, Routes, Route} from 'react-router-dom';

export default function App() {
  const [book, setBook] = useState([])
  const marking=(e)=>{
    setBook(book.push({
      id: item.id,
      image_url: item.image_url || item.brand_image_url,
      title: item.title || item.brand_name,
      discountPercentage: item.discountPercentage,
      follower: item.follower? item.follower : item.price+`원`,
      sub_title: item.sub_title
    }))
  }

  const [filter, setFilter] = useState('')
  const setFilterHandler = (e) => {
    setFilter(e.target.name)
    console.log(`type:`, e.target.name)
  }

  const [list, setList] = useState([])
  useEffect(()=>{
    fetch('http://cozshopping.codestates-seb.link/api/v1/products')
    .then(res=>res.json())
    .then(list=>setList(list))
    .catch(err=>console.log(err))
  },[])
  return (
    <BrowserRouter>
      <Header />
      <Routes>
      <Route path="*" element={<Main list={list} book={book} setBook={setBook}/>} />
      <Route path="/products/list" element={<ProdectsList filter={filter} setFilter={setFilter} setFilterHandler={setFilterHandler} list={list} book={book} setBook={setBook} />} />
      <Route path="/bookmark" element={<Bookmark filter={filter} setFilter={setFilter} setFilterHandler={setFilterHandler} list={list} />} book={book} setBook={setBook} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}
