import Main from './page/Main';
import ProdectsList from './page/ProductList';
import Bookmark from './page/Bookmark';

import Header from './component/Header';
import Footer from './component/Footer';

import { useEffect, useState } from 'react';
import {BrowserRouter, Routes, Route} from 'react-router-dom';

export default function App() {
  const [filter, setFilter] = useState("")
  const setFilterHandler = (e) => {
    setFilter(e.target.name)
    console.log(`type:`, e.target.name)
  }

  const [list, setList] = useState([])
  const [productList, setPro] = useState([]);
  const [categoryList, setCat] = useState([]);
  const [exhibitionList, setExh] = useState([]);
  const [brandList, setBra] = useState([]);
  useEffect(()=>{
    fetch('http://cozshopping.codestates-seb.link/api/v1/products')
      .then(res=>res.json())
      .then(list=>{
        setList(list)
        setPro(list.filter(el=>el.type==='Product'))
        setCat(list.filter(el=>el.type==='Category'))
        setExh(list.filter(el=>el.type==='Exhibition'))
        setBra(list.filter(el=>el.type==='Brand'))
        return getLists()
      })
      .catch(err=>console.log(err))
  },[])
  return (
    <BrowserRouter>
      <Header />
      <Routes>
      <Route path="*" element={<Main filter={filter} list={list} productList={productList} categoryList={categoryList} exhibitionList={exhibitionList} brandList={brandList} />} />
      <Route path="/products/list" element={<ProdectsList filter={filter} setFilter={setFilter} setFilterHandler={setFilterHandler} list={list} productList={productList} categoryList={categoryList} exhibitionList={exhibitionList} brandList={brandList}/>} />
      <Route path="/bookmark" element={<Bookmark filter={filter} setFilter={setFilter} setFilterHandler={setFilterHandler} list={list} />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}
